import React from "react";
import menu from "../data"

const Menu = () => {
  const [menuInfos, setMenuInfos]= useState(menu)
  return (
    <div className="section-center">
      {menuInfos.map((menuInfo) => {
        return (
          <article key={menuInfo.id} className="menu-item">
            <img src={menuInfo.img} alt={menuInfo.title} className="photo" />
            <div className="item-info">
              <header>
                <h4>{menuInfo.title}</h4>
                <h4 className="price">${menuInfo.price}</h4>
              </header>
              <p className="item-text">{menuInfo.desc}</p>
            </div>
          </article>
        );
      })}
    </div>

  );
};

export default Menu;
