import React, { useState } from "react";
import menu from "../data"
import { Link} from "react-router-dom"
import Categorie from "./Categorie";

const Categories = () => {
  const [menuInfos, setMenuInfos]= useState(menu)
  const uniqCategorie= [...new Set(menuInfos.map((menuInfo) =>
    menuInfo.category
  ))]




  const categorieHandel= (item)=>{
    let finalItems= menuInfos.filter(menuInfo=>(
      menuInfo.category === item
    ))
    finalItems.map((finalItem)=> <Categorie img={finalItem.img} title={finalItem.title}
    price={finalItem.price} desc={finalItem.desc} id={finalItem.id} />)





  }



  return (
    <div className="btn-container"> 
      {uniqCategorie.map((item,index) =>(

        <Link to="/Categorie">
        <button onClick={()=>categorieHandel(item)} key={index}
        type="button"
        className="filter-btn"
      >
        {item}
      </button>
      </Link>


      ))}
      <Link to="/Categorie">
      <button
        type="button"
        className="filter-btn"
      >
        All
      </button>
      </Link>

    </div>
  );
};

export default Categories;
