import React, { useState } from "react";
import menu from "../data"


const Categories = ({uniqCategorie,categorieHandel}) => {

  const [mainCategorie, setMainCategorie]= useState("all")


  return (
    <div className="btn-container"> 
      {uniqCategorie.map((item,index) =>(

        <button onClick={()=>categorieHandel(item)} key={index}
        type="button"
        // className="filter-btn"
        className={
          mainCategorie === item ? "filter-btn highlight" : "filter-btn"
        }
      >
        {item}
      </button>



      ))}




    </div>
  );
};

export default Categories;
