import React, { useState } from 'react';
import menu from './data';

function App() {

  const [menuInfos, setMenuInfos]= useState(menu)

  return (
    <main>
      <section className="menu section">
        <div className="title">
          <h2>our menu</h2>
          <div className="underline">
            {menuInfos.map(menuInfo =>(
              <div className=' section-center'>
                <img className='photo' src={menuInfo.img} />
                <div className=''>
                  <div>
                    <p>{menuInfo.title}</p>
                    <span className='price'>{menuInfo.price}</span>
                  </div> 
                    <p>{menuInfo.desc}</p>

                </div>

              </div>
            ))}

          </div>
        </div>
      </section>
    </main>
  );
}

export default App;
