import React, { useState } from 'react';
import menu from './data';

function App() {

  const [menuInfos, setMenuInfos]= useState(menu)

  return (
    <main>
      <section className="menu section">
        <div className="title">
          <h2>our menu</h2>
          <div className="underline">
            {menuInfos.map(menuInfo =>(
              <div className=' section-center'>
                <div className='menu-item'>
                <img className='photo' src={menuInfo.img} />
                <header className='item-info'>

                    <h4 className='item-info '>{menuInfo.title}</h4>
                    <span className='price'>{menuInfo.price}</span>
                    </header>  
                    <p className='item-text'>{menuInfo.desc}</p>

                <header/> 
                </div>

              </div>
            ))}

          </div>
        </div>
      </section>
    </main>
  );
}

export default App;
