import React, { useState } from "react";
import menu from "../data"

const Categories = () => {
  const [menuInfos, setMenuInfos]= useState(menu)

  return (
    <div className="btn-container">
      {[...new Set(menuInfos.map(menuInfo =>
        menuInfo.category
      ))]}

      {[...new Set(menuInfos.map(menuInfo =>
        menuInfo.category
      ))].map(item =>{
        <button
        type="button"
        // highlight class  for highlight main category
        className="filter-btn"
      >

        item
      </button>

      })}


      <button
        type="button"
        // highlight class  for highlight main category
        className="filter-btn"
      >

        All
      </button>

    </div>
  );
};

export default Categories;
