import React, { useState } from "react";
import menu from "../data"
import Categorie from "./Categorie";

const Categories = () => {
  const [menuInfos, setMenuInfos]= useState(menu)
  const uniqCategorie= [...new Set(menuInfos.map((menuInfo) =>
    menuInfo.category
  ))]





    const categorieHandel = (item) => {
      let finalItems = menuInfos.filter((menuInfo) => menuInfo.category === item);

      return finalItems.map((finalItem) => (
        <Categorie
          key={finalItem.id} // Don't forget to provide a unique key for each item in the map function
          img={finalItem.img}
          title={finalItem.title}
          price={finalItem.price}
          desc={finalItem.desc}
          id={finalItem.id}
        />
      ));




  }



  return (
    <div className="btn-container"> 
      {uniqCategorie.map((item,index) =>(

        <button onClick={()=>categorieHandel(item)} key={index}
        type="button"
        className="filter-btn"
      >
        {item}
      </button>



      ))}

      <button
        type="button"
        className="filter-btn"
      >
        All
      </button>


    </div>
  );
};

export default Categories;
